f.p=fontsize(6);

xaxis(0,4,Ticks(f, 1.0));

yaxis(-4,4,Ticks(f, 1.0));

dot((3,3));
dot((3,-3));
dot((1,-3));
label("$A$", (3,3), W);
label("$A'$", (3,-3), W);
label("$A''$", (1,-3), W);
[/asy]

