B = (5,0,0);
C = (5,6,0);
D= (0,6,0);
EE = (0,0,4);
F = B+EE;
G = C + EE;
H = D + EE;
draw(B--C--D);
draw(B--A--D,dashed);
draw(EE--F--G--H--EE);
draw(B--H--A--EE,dashed);
draw(A--C,dashed);
draw(B--F);
draw(C--G);
